import { DiscussServiceClient } from "@google-ai/generativelanguage";
import { GoogleAuth } from "google-auth-library";
import MessageAPI from './MessageAPI.mjs'
import { logger } from '../logger.mjs';

class BisonMessageAPI extends MessageAPI {
    constructor() {
        super();

        const { PALM_API_KEY, PALM_MODEL, PALM_TEMPERATURE } = process.env;

        if (!PALM_API_KEY || !PALM_MODEL || !PALM_TEMPERATURE) {
            throw new Error("Environment variables are not set correctly.");
        }

        this.API_KEY = PALM_API_KEY;
        this.MODEL = PALM_MODEL;
        this.TEMPERATURE = PALM_TEMPERATURE;

        try {
            this.googleAuth = new GoogleAuth();
            this.client = new DiscussServiceClient({
                authClient: this.googleAuth.fromAPIKey(this.API_KEY),
            });
        } catch (error) {
            logger.error(`Failed to initialize GoogleAuth and DiscussServiceClient: ${error}`);
            throw error;
        }
    }

    formatPromptMessage(messages) {
        const systemMessages = messages.filter(msg => msg.role === 'system').map(msg => msg.content);
        const otherMessages = messages.filter(msg => msg.role !== 'system').map(msg => ({ content: msg.content }));

        return {
            context: systemMessages.join('. '),
            messages: otherMessages,
        };
    }

    async sendRequest(messages, options = {}) {
        console.log('messages', JSON.stringify(messages, null, 2));
        logger.info(`Bison Received message request: ${JSON.stringify(messages)}`);
        logger.info(`Bison Received options: ${JSON.stringify(options)}`);
        const prompt = this.formatPromptMessage(messages);
        console.log('prompt', JSON.stringify(prompt, null, 2));
        try {
            const result = await this.client?.generateMessage({
                model: options.userModel || this.MODEL,
                temperature: options.temperature || this.TEMPERATURE,
                maxOutputTokens: options.maxTokens || this.MAX_TOKENS,
                candidateCount: 1,
                prompt,
            });
            const response = {
                choices: result?.[0]?.candidates?.map(candidate => ({
                    message: {
                        content: candidate.content,
                        role: 'assistant',
                    },
                })),
            };
            logger.info(`Received response from Google: ${JSON.stringify(result)}`);
            return response;
        } catch (error) {
            logger.error(`Failed to send the request: ${error}`);
            throw error;
        }
    }
}

export default BisonMessageAPI;
