import { logger } from "../logger.mjs";
import fetch from "node-fetch";
import { TextDecoder } from "util";
import MessageAPI from "./MessageAPI.mjs";

const OPEN_AI_API_URL = "https:

const { OPENAI_MODEL, OPENAI_API_KEY, OPENAI_MAX_TOKENS, OPENAI_TEMPERATURE } =
  process.env;

function checkEnvVariables() {
  if (
    !OPENAI_MODEL ||
    !OPENAI_API_KEY ||
    !OPENAI_MAX_TOKENS ||
    !OPENAI_TEMPERATURE
  ) {
    throw new Error("Environment variables are not set correctly.");
  }
}

checkEnvVariables();

class OpenAIMessageAPI extends MessageAPI {
  constructor(userModel) {
    super();

    this.MODEL = userModel || OPENAI_MODEL;
    this.API_KEY = OPENAI_API_KEY;
    this.TEMPERATURE = parseFloat(OPENAI_TEMPERATURE);
    this.MAX_TOKENS = parseInt(OPENAI_MAX_TOKENS, 10);

    if (isNaN(this.MAX_TOKENS) || isNaN(this.TEMPERATURE)) {
      throw new Error("Invalid max token or temperature value");
    }
  }

  _prepareHeaders() {
    return {
      Authorization: `Bearer ${this.API_KEY}`,
      "Content-Type": "application/json",
    };
  }

  _prepareOptions(body) {
    return {
      method: "POST",
      headers: this._prepareHeaders(),
      body: JSON.stringify(body),
    };
  }

  async sendRequest(messages, { userModel, maxTokens, temperature } = {}) {
    logger.info("Received message request:", messages);
    logger.info("Received options:", { userModel, maxTokens, temperature });

    const updatedMessages = messageToOpenAIFormat(messages);
    logger.info("OpenAI request updatedMessages:", updatedMessages);
    console.log('updatedMessages', JSON.stringify(updatedMessages, null, 2));

    const requestOptions = this._prepareOptions({
      model: userModel || this.MODEL,
      messages: updatedMessages,
      max_tokens: maxTokens || this.MAX_TOKENS,
      temperature: temperature || this.TEMPERATURE,
    });
    logger.info("OpenAI request options:", requestOptions);
    const response = await fetch(OPEN_AI_API_URL, requestOptions);

    if (!response.ok) {
      const text = await response.text();
      console.log('text', text);
      logger.error("Open AI API response error: ", text);
      throw new Error("An error occurred during the post");
    }

    const data = await response.json();
    console.log('data', JSON.stringify(data, null, 2));
    logger.info("OpenAI response:", data);

    return data;
  }

  async sendRequestStreamResponse(
    messages,
    { userModel, maxTokens, temperature } = {},
    resClient
  ) {
    logger.info("OpenAI request:", messages);

    const updatedMessages = messageToOpenAIFormat(messages);

    const requestOptions = this._prepareOptions({
      model: userModel || this.MODEL,
      messages: updatedMessages,
      max_tokens: maxTokens || this.MAX_TOKENS,
      temperature: temperature || this.TEMPERATURE,
      stream: true,
    });
    logger.info(
      "---------------OpenAI request options:",
      requestOptions
    );

    let data = "";
    let lastchunk = "";
    const response = await fetch(OPEN_AI_API_URL, requestOptions)
      .then((response) => response.body)
      .then((res) =>
        res.on("readable", () => {
          let chunk;
          while (null !== (chunk = res.read())) {
            let decorechunk = chunk.toString();
            if (!decorechunk.startsWith("data:")) {

              decorechunk = lastchunk + decorechunk;
            }


            const lines = decorechunk.split("\n");
            let done = false;

            const parsedLines = lines
              .map((line) => {
                if (line.startsWith("data: ")) {
                  lastchunk = line;

                }

                if (line.replace(/^data: /, "").trim() === "[DONE]") {
                  done = true;
                }
                return line.replace(/^data: /, "").trim();
              }) 
              .filter((line) => line !== "" && line !== "[DONE]") 
              .map((line) => {
                try {
                  return JSON.parse(line);
                } catch (error) {

                  return null;
                }
              });
            if (done) {
              resClient.end();
              break;
            }
            for (const parsedLine of parsedLines) {
              if (parsedLine?.choices?.[0]?.delta?.content) {
                const { content } = parsedLine.choices[0].delta;
                data += content;

                resClient.write(content);
              }
            }
          }
        })
      )
      .catch((err) => logger.error(err));
    return data;
  }
}

export default OpenAIMessageAPI;
function messageToOpenAIFormat(messages) {
  const updatedMessages = messages.map(({ messageId, modelName, ...message }) => ({
    ...message,
    role:
      message.role === "bot"
        ? "assistant"
        : message.role === "context"
        ? "system"
        : message.role,
  }));
  logger.info("OpenAI request updatedMessages:", updatedMessages);
  return updatedMessages;
}

