import React, { useState, useEffect, useRef } from "react";
import { v4 as uuidv4 } from "uuid";
import moment from "moment";
import { useTranslation } from "react-i18next";

import {
  useConversations,
  updateConversation,
  postConversation,
} from "./api/conversationService";
import Sidebar from "./components/layout/Sidebar/Sidebar";
import ConversationHeader from "./components/conversation/ConversationHeader/ConversationHeader";
import ConversationBody from "./components/conversation/ConversationBody/ConversationBody";
import ConversationFooter from "./components/conversation/ConversationFooter/ConversationFooter";
import LoginDialog from "./components/common/LoginDialog/LoginDialog";
import { sendMessage, sendMessageStreamResponse } from "./api/messageService";
import { fetchModels } from "./api/modelService"; 

import "./App.css";
import "./styles/main.scss";
import { use } from "i18next";

function App() {
  const [user, setUser] = useState(null);
  const [conversations, setConversations] = useState([]);
  const [currentConversation, setCurrentConversation] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [newBotMessage, setNewBotMessage] = useState({});
  const [isStreaming, setIsStreaming] = useState(false);
  const [models, setModels] = useState([]);
  const [loadingModels, setLoadingModels] = useState(true);
  const [modelsError, setModelsError] = useState(null);
  const { t } = useTranslation();
  const fetchedConversations = useConversations(user ? user.userId : null);
  const [isUseStreamingAPI, setIsUseStreamingAPI] = useState(false);
  const [sendNewMessage, setSendNewMessage] = useState(false);
  const [isSelectionChanged, setIsSelectionChanged] = useState(false);
  const handleLogin = async (user) => {
    setUser(user);
    setIsLoggedIn(true);
  };
  const prevConversationRef = useRef(currentConversation);

  useEffect(() => {
    console.log("conversations", conversations);
  }, [conversations]);

  useEffect(() => {
    if (currentConversation && !isStreaming && !isSelectionChanged) {
      if (prevConversationRef.current !== currentConversation) {
        updateConversation(currentConversation);
      }
    }
    prevConversationRef.current = currentConversation;
    setIsSelectionChanged(false);

  }, [currentConversation, isStreaming]);

  useEffect(() => {
    setConversations(fetchedConversations);
  }, [fetchedConversations]);

  useEffect(() => {
    const loadModels = async () => {
      try {
        setLoadingModels(true);
        setModelsError(null);
        const modelsData = await fetchModels();
        setModels(modelsData);
      } catch (error) {
        setModelsError(error.message);
      } finally {
        setLoadingModels(false);
      }
    };

    if (isLoggedIn) {
      loadModels();
    }
  }, [isLoggedIn]);

  useEffect(() => {
    const sendMsgAndUpdateConversation = async () => {
      if (!currentConversation || !newBotMessage || !sendNewMessage) {
        return;
      }
      setIsStreaming(false);
      const data = await sendMessage(
        currentConversation,
        user,
        newBotMessage,
        setCurrentConversation
      );

      setCurrentConversation((prevConversation) => ({
        ...prevConversation,
        messages: prevConversation.messages.map((message) =>
          message.messageId === newBotMessage.messageId
            ? { ...message, content: data?.content }
            : message
        ),
      }));
      setSendNewMessage(false);
    };

    sendMsgAndUpdateConversation();
  }, [sendNewMessage]);

  useEffect(() => {
    if (!currentConversation) {
      return;
    }

    setConversations((prevConversations) =>
      prevConversations.map((conversation) =>
        conversation.conversationId === currentConversation.conversationId
          ? currentConversation
          : conversation
      )
    );
  }, [currentConversation]);

  useEffect(() => {
    if (!currentConversation || !newBotMessage || !currentConversation.messages) {
      return;
    }
    if (user.settings.isStreamResponse) {
      let isCancelled = false;

      const sendStreamResponse = async () => {
        if (
          !isCancelled &&
          user &&
          currentConversation &&
          newBotMessage &&
          !isStreaming
        ) {
          setIsStreaming(true);
          try {
            await sendMessageStreamResponse(
              user,
              currentConversation,
              setCurrentConversation,
              newBotMessage,
              setIsStreaming
            );
          } catch (err) {
            console.error("An error occurred:", err);
          }
        }
      };

      sendStreamResponse();
      return () => {
        isCancelled = true;
      };
    }

  }, [newBotMessage]);

  const createNewConversation = () => {
    const conversationId = uuidv4();
    const title = moment().format(t("title_date_format"));

    const defaultContext =
      user && user.settings && user.settings.contexts
        ? user.settings.contexts.find((ctx) => ctx.isDefault)
        : null;

    const defaultContextMessage = {
      role: "context",
      content: defaultContext ? defaultContext.text : "",
      messageId: uuidv4(),
    };

    const newConversation = {
      title,
      conversationId,
      userId: user.userId,
      messages: [defaultContextMessage], 
    };
    postConversation(newConversation);
    setConversations((prev) => [...prev, newConversation]);
    setCurrentConversation(newConversation);
  };

  if (!isLoggedIn) {
    return <LoginDialog setUser={handleLogin} />;
  }

  const handleNewUserMessage = async (input) => {
    setIsStreaming(false);
    if (!currentConversation) createNewConversation();

    const newUserMessage = {
      content: input,
      role: "user",
      messageId: uuidv4(),
    };

    const newBotMessage = {
      content: "",
      role: "bot",
      messageId: uuidv4(),
      modelName: user.settings.model,
    };

    setCurrentConversation((prevConversation) => {
      if (prevConversation && prevConversation.messages) {
        const updatedMessages = [
          ...prevConversation.messages,
          newUserMessage,
          newBotMessage,
        ];

        setConversations((prevConversations) =>
          prevConversations.map((conversation) =>
            conversation.conversationId === prevConversation.conversationId
              ? { ...prevConversation, messages: updatedMessages }
              : conversation
          )
        );

        let updatedMessagesWithoutIds = updatedMessages.map(
          ({ messageId, ...rest }) => rest
        );
        updatedMessagesWithoutIds.map((message) => {
          if (message.role === "bot") {
            return { ...message, role: "assistant" };
          }
          return message;
        });

        return { ...prevConversation, messages: updatedMessages }; 
      }
    });
    setNewBotMessage(newBotMessage);
    if (!isUseStreamingAPI) {
      setSendNewMessage(true);
    }
  };

  return (
    <div className="App">
      <Sidebar
        conversations={conversations}
        currentConversation={currentConversation}
        setCurrentConversation={setCurrentConversation}
        setConversations={setConversations}
        createNewConversation={createNewConversation}
        setIsSelectionChanged={setIsSelectionChanged}
      />
      <div className="conversation-section">
        <ConversationHeader
          user={user}
          setUser={setUser}
          models={models}
          loadingModels={loadingModels}
          modelsError={modelsError}
          isLoggedIn={isLoggedIn}
          setIsLoggedIn={setIsLoggedIn}
        />
        <ConversationBody
          currentConversation={currentConversation}
          setCurrentConversation={setCurrentConversation}
          setConversations={setConversations}
        />
        <ConversationFooter
          onSendMessage={handleNewUserMessage}
          isStreaming={isStreaming}
        />
      </div>
    </div>
  );
}

export default App;

