import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import "./ContextTab.scss";

function ContextTab({ user, setUser }) {
  const [selectedItemIndex, setSelectedItemIndex] = useState(null);
  const [contexts, setContexts] = useState(user.settings.contexts);

  useEffect(() => {
    setContexts(user.settings.contexts);
  }, [user.settings.contexts]);

  useEffect(() => {
    setUser((prevUser) => ({
      ...prevUser,
      settings: { ...prevUser.settings, contexts },
    }));
  }, [contexts, setUser]);

  const handleContextChange = (index, key, value) => {
    const updatedContexts = contexts.map((context, idx) => {
      if (idx === index) {
        return { ...context, [key]: value };
      }
      if (key === "isDefault" && value) {
        return { ...context, isDefault: false };
      }
      return context;
    });
    setContexts(updatedContexts);
  };

  const handleAdd = () => {
    const newItem = {
      name: "",
      text: "",
      contextId: `${Date.now()}`,
      isDefault: false,
    };
    setContexts([...contexts, newItem]);
    setSelectedItemIndex(contexts.length);
  };

  const handleDelete = () => {
    const updatedContexts = contexts.filter(
      (_, idx) => idx !== selectedItemIndex
    );
    setContexts(updatedContexts);
    setSelectedItemIndex(null);
  };

  const handleSetDefault = (index) => {
    handleContextChange(index, "isDefault", true);
  };

  return (
    <div className="context-tab">
      <div className="content">
        <div className="left-panel">
          <ul className="list">
            {contexts.map((context, index) => (
              <li
                className={`list-item ${
                  index === selectedItemIndex ? "active" : ""
                }`}
                key={context.contextId}
                onClick={() => setSelectedItemIndex(index)}
              >
                {context.name}
                {context.isDefault && <span> (Default)</span>}
              </li>
            ))}
          </ul>
        </div>
        <div className="right-panel">
          {selectedItemIndex !== null && (
            <div className="input-container">
              <input
                type="text"
                value={contexts[selectedItemIndex].name}
                onChange={(e) =>
                  handleContextChange(selectedItemIndex, "name", e.target.value)
                }
                placeholder="Item name"
              />
              <textarea
                value={contexts[selectedItemIndex].text}
                onChange={(e) =>
                  handleContextChange(selectedItemIndex, "text", e.target.value)
                }
                placeholder="Multiline text"
              />
              <button onClick={() => handleSetDefault(selectedItemIndex)}>
                Set as Default
              </button>
            </div>
          )}
        </div>
      </div>
      <div className="footer">
        <button onClick={handleAdd}>Add</button>
        <button
          onClick={() =>
            selectedItemIndex !== null &&
            handleContextChange(selectedItemIndex, "name", "")
          }
          disabled={selectedItemIndex === null}
        >
          Clear
        </button>
        <button onClick={handleDelete} disabled={selectedItemIndex === null}>
          Delete
        </button>
      </div>
    </div>
  );
}

ContextTab.propTypes = {
  user: PropTypes.shape({
    settings: PropTypes.shape({
      contexts: PropTypes.arrayOf(
        PropTypes.shape({
          name: PropTypes.string,
          contextId: PropTypes.string,
          text: PropTypes.string,
          isDefault: PropTypes.bool,
        })
