import React, { useState, useCallback, useEffect } from "react";
import TextareaAutosize from "react-textarea-autosize";
import PropTypes from "prop-types";
import { useTranslation } from "react-i18next";
import ReactMarkdown from "react-markdown";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { solarizedlight } from "react-syntax-highlighter/dist/esm/styles/prism";
import { CopyToClipboard } from "react-copy-to-clipboard";
import gfm from "remark-gfm";

import ModalDialog from "../../common/ModalDialog/ModalDialog";
import "./MessageItem.scss";

const CodeBlock = ({ language, value }) => {
  const [isCopied, setIsCopied] = useState(false);
  const { t } = useTranslation();

  const onCopy = useCallback(() => {
    setIsCopied(true);
  }, []);

  useEffect(() => {
    if (isCopied) {
      const timer = setTimeout(() => setIsCopied(false), 2000);
      return () => clearTimeout(timer);
    }
  }, [isCopied]);

  return (
    <div className="code-block-container">
      <div className="code-block-header">
        <span>{language}</span>
        <CopyToClipboard text={value} onCopy={onCopy}>
          <button
            className="copy-code-button"
            title={t("copy_to_clipboard_title")}
          >
            {isCopied ? t("copied") : t("copy_to_clipboard")}
          </button>
        </CopyToClipboard>
      </div>
      <SyntaxHighlighter
        language={language || "text"}
        style={solarizedlight}
        customStyle={{ marginTop: "0" }}
      >
        {value || ""}
      </SyntaxHighlighter>
    </div>
  );
};


const renderers = {
  code: ({ node, inline, className, children, ...props }) => {

    const match = /language-(\w+)/.exec(className || "");
    return !inline && match ? (
      <CodeBlock
        value={String(children).replace(/\n$/, "")}
        language={match[1]}
        {...props}
      />
    ) : (
      <code className={className} {...props}>
        {children}
      </code>
    );
  },
};

function MessageItem({ message, onDelete, onEdit }) {
  const { t } = useTranslation();
  const [copied, setCopied] = useState(false);
  const [isModalOpen, setModalOpen] = useState(false);
  const [isEditing, setEditing] = useState(false);
  const [editedMessage, setEditedMessage] = useState(message.content);

  const resetCopyState = useCallback(() => {
    setCopied(false);
  }, []);

  const handleModalClose = useCallback(() => setModalOpen(false), []);
  const handleDeleteClick = useCallback(() => setModalOpen(true), []);

  const handleDeleteConfirm = useCallback(() => {
    setModalOpen(false);
    onDelete();
  }, [onDelete]);

  const handleEditClick = useCallback(() => {
    setEditedMessage(message.content); 
    setEditing(true);
  }, [setEditing, message.content]);


  const handleEditConfirm = useCallback(() => {
    setEditing(false);
    onEdit(editedMessage);
  }, [onEdit, editedMessage]);

  const handleEditChange = useCallback(
    (event) => {
      setEditedMessage(event.target.value);
      event.target.style.height = "auto";
      event.target.style.height = `${event.target.scrollHeight}px`;
    },
    [setEditedMessage]
  );

  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
    } catch (err) {
      console.error("Failed to copy text: ", err);
    }
    setCopied(true);
    setTimeout(resetCopyState, 2000);
  };

  function handleOnKeyDown() {
    return (event) => {
      if (event.key === "Enter" && !event.shiftKey) {
        event.preventDefault();
        handleEditConfirm();
      } else if (event.key === "Escape") {
        setEditing(false);
        setEditedMessage(message.content);
      }
    };
  }
  return (
    <>
      {isModalOpen && (
        <ModalDialog
          isOpen={isModalOpen}
          onClose={handleModalClose}
          onCancel={handleModalClose}
          onConfirm={handleDeleteConfirm}
        >
          <h4>{t("delete_message_title")}</h4>
          <h2>{t(message.role)}</h2>
          <p>{message.content.substring(0, 200)}</p>
        </ModalDialog>
      )}
      <div className="message-item">
        <div className="message-type">{t(message.role)}</div>
        <div className="message-content">
          {isEditing ? (
            <TextareaAutosize
              className="message-content-edit"
              value={editedMessage}
              onChange={handleEditChange}
              onBlur={handleEditConfirm}
              onKeyDown={handleOnKeyDown()}
              autoFocus
            />
          ) : (
            <ReactMarkdown components={renderers} remarkPlugins={[gfm]}>
              {message.content}
            </ReactMarkdown>
          )}
        </div>
        <div className="message-actions">
          <div className="message-tool-bar">
            <button
              className="action-button"
              title={t("copy_to_clipboard_title")}
              onClick={() => copyToClipboard(message.content)}
            >
              {copied ? t("copied") : t("copy_to_clipboard")}
            </button>
            <button
              className="action-button"
              title={t("delete_title")}
              onClick={handleDeleteClick}
            >
              {t("delete")}
            </button>
            <button
              className="action-button"
              title={t("edit_title")}
              onClick={handleEditClick}
            >
              {t("edit")}
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

MessageItem.propTypes = {
  message: PropTypes.shape({
    content: PropTypes.string,
    role: PropTypes.string,
  }).isRequired,
  onDelete: PropTypes.func.isRequired,
  onEdit: PropTypes.func.isRequired,
};

export default MessageItem;

