import React, { useState, useEffect, useRef, useCallback } from "react";
import { useTranslation } from "react-i18next";
import { BeatLoader } from "react-spinners";
import hljs from 'highlight.js';

import "./ConversationFooter.scss";

const ConversationFooter = ({ onSendMessage, isStreaming }) => {
  const { t } = useTranslation();
  const [input, setInput] = useState("");
  const textareaRef = useRef(null);

  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${textarea.scrollHeight}px`;
    }
  }, [input]);

  const handleSend = useCallback(async () => {
    const trimmedInput = input.trim();
    if (trimmedInput) {
      await onSendMessage(trimmedInput);
      setInput("");
    }
  }, [input, onSendMessage]);

  useEffect(() => {

    if (textareaRef.current) {
      textareaRef.current.scrollTop = textareaRef.current.scrollHeight;
    }
  }, [input]);

  async function handlePaste() {
    try {
      const clipboardText = await navigator.clipboard.readText();


      const detectedLanguage = hljs.highlightAuto(clipboardText);
      const language = detectedLanguage.language || 'plaintext';

      const formattedText = `\`\`\`${language}\n${clipboardText}\n\`\`\``;

      const cursorPosition = textareaRef.current.selectionStart;
      const textBeforeCursor = input.substring(0, cursorPosition);
      const textAfterCursor = input.substring(cursorPosition);

      setInput(textBeforeCursor + formattedText + textAfterCursor);


      textareaRef.current.focus();
      const newPosition = (textBeforeCursor + formattedText).length;
      textareaRef.current.setSelectionRange(newPosition, newPosition);
    } catch (err) {
      console.error('Failed to read clipboard contents: ', err);
    }
  }

  return (
    <div className="conversation-footer">
      <textarea
        ref={textareaRef}
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => {
          if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSend();
          }
        }}
        placeholder={t("type_a_message")}
        rows={1}
        style={{ maxHeight: "100px" }}
        disabled={isStreaming}
      />
      <button
        title={t("send")}
        onClick={handleSend}
        disabled={!input.trim() || isStreaming}
      >
        {isStreaming ? <BeatLoader color="white" size="6px" /> : "➢"}
      </button>
      <div className="spacer" />
      <button
        title={t("paste_title")}
        onClick={handlePaste}
        disabled={isStreaming}
      >
        {t("paste")}
      </button>
    </div>
  );
};

export default ConversationFooter;

