  const updateConversation = (updatedConversation) => {
    setConversations((prev) =>
      prev.map((c) =>
        c.conversationId === updatedConversation.conversationId
          ? updatedConversation
          : c
      )
    );
    updateConversationApi(updatedConversation);
  };

