import { useCallback } from 'react';
import '../stylesheets/index.css';
import { SECOND, RESPONSE_ERROR_MSG } from '../constants';
const handleResponse = async (response) => {
    if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error);
    }
    return response;

};

function copyMessages(conversations, conversationId, userMessage) {
    let conversation = conversations
        .filter(conversation => conversation.conversationId === conversationId);
    let messages;
    if (conversation.length === 0) {
        messages = [];
    }
    else {
        messages = conversation[0].messages;
    }
    const messagesCopy = messages
        .map(message => ({
            role: message.role,
            content: message.content,
        }));

    messagesCopy.push({
        role: 'user',
        content: userMessage,
    });
    return messagesCopy;
}

const useMessageHandler = ({
    conversations,
    userMessage,
    username,
    userDetails,
    handleSendMessage,
    setConversations,
    conversationId,
    currentTitle,
    setUserMessage,
    setIsLoading,
    setErrorMessage,
    setDuration,
    setPromptTokens,
    setCompletionTokens,
    setTotalTokens,
}) => {
    const userMessageSubmit = useCallback(async (event) => {
        event.preventDefault();
        setIsLoading(true);
        const handleUpdateConversations = async (username, userDetails, currentTitle) => {
            const messages = copyMessages(conversations, conversationId, userMessage, username, userDetails);
            const startTime = Date.now();
            const response = await handleSendMessage(messages, username, userDetails, conversationId, currentTitle);
            const data = await handleResponse(response);

            let filteredUserMessage = data.filteredUserMessage;
            let currentConversation = getCurrentConversation(conversations, conversationId, currentTitle);
            addNewResponseMessage(currentConversation, data, filteredUserMessage, conversations, setPromptTokens, setCompletionTokens, setTotalTokens, setConversations, setIsLoading);
            const endTime = Date.now();
            setDuration((endTime - startTime) / SECOND);
            setIsLoading(false);
            setUserMessage('');
        };
        try {
            await handleUpdateConversations(username, userDetails, currentTitle);
        } catch (error) {
            setIsLoading(false);
            setErrorMessage('Bad things happened.');
            throw error;
        }
    }, [
        setIsLoading,
        setPromptTokens,
        setCompletionTokens,
        setTotalTokens,
        setDuration,
        conversations,
        conversationId,
        userMessage,
        handleSendMessage,
        setUserMessage,
        setConversations,
        username,
        userDetails,
        currentTitle,
        setErrorMessage
    ]);

    return userMessageSubmit;
};


export default useMessageHandler;

function addNewResponseMessage(currentConversation, data, filteredUserMessage, conversations, setPromptTokens, setCompletionTokens, setTotalTokens, setConversations, setIsLoading) {
    let currentMessages = currentConversation[0].messages;
    if (data && data.filteredUserMessage && data.aiMessage) {
        const newMessages = [...currentMessages, filteredUserMessage, data.aiMessage];
        const newConversations = conversations.map(conversation => conversation.conversationId === currentConversation[0].conversationId
            ? { ...conversation, messages: newMessages }
            : conversation
        );
        if (data.aiMessage) {
            setPromptTokens(data.aiMessage.prompt_tokens);
            setCompletionTokens(data.aiMessage.completion_tokens);
            setTotalTokens(data.aiMessage.total_tokens);
        }
        setConversations(newConversations);
    } else {
        setIsLoading(false);
        throw new Error(RESPONSE_ERROR_MSG);
    }
}

function getCurrentConversation(conversations, conversationId, currentTitle) {
    let currentConversation = conversations
        .filter(conversation => conversation.conversationId === conversationId);
    if (currentConversation.length === 0) {
        currentConversation = [{
            conversationId: conversationId,
            title: currentTitle,
            messages: []
        }];
        conversations.unshift(currentConversation[0]);

    }
    return currentConversation;
}

